import React, { useEffect, useState } from 'react'
import {ThumbUpOffAlt, ThumbUpAlt, Comment} from "@mui/icons-material"
import axios from 'axios'
import { Link } from 'react-router-dom'

const Blogposts = () => {
    const [posts, setPosts] = useState([])
    const [comment, setComment] = useState(false)
    const [like, setLike] = useState(false)
    const handlecomment = ()=>{
        setComment(comment? false:true)
    }

    const handleLikes = ()=>{
        setLike(like? false:true)
    }

    const post = posts[posts.length-1]
    

useEffect(()=>{
const getPosts = async ()=>{
    try {
        const res = await axios.get("http://localhost:3000/api/post")
    setPosts(res.data)
    } catch (error) {
        console.log(error)
    }
    
}
getPosts()
},[])

  return (
    <div className='flex'>
      <div className='basis-3/4 flex flex-col border-r-2 p-4 justify-center items-center'>
      <img className='w-80 h-80 mb-4' src="data:image/jpeg;base64," alt="pastor chris" />
      <h2 className='text-2xl font-bold text-center mb-2'>{post?.Title}</h2>
      <p className='italic mb-4'>
    {post?.Body}
      </p>
      <div className='flex'>
        <span>5</span>{like? <ThumbUpAlt onClick={handleLikes}/> :<ThumbUpOffAlt onClick={handleLikes}/>}
        <div className='ml-4'>
        <span>5</span><Comment onClick={handlecomment}/>
        </div>
        
      </div>
      {comment && <input type="text" placeholder='Leave a comment.' className='w-1/2 h-40 rounded-sm border-2 p-2'/>}
         
      </div>
      <div className='basis-1/4 p-2'>
      <h2 className='text-2xl font-bold text-center mb-4'>Relatted posts</h2>
      {posts.map((item)=>(
        <Link to={`/blogpost/${item._id}`}>
         <div className='flex mb-4 justify-center items-center'>
         <img className='w-10 h-10 md:w-20 md:h-20 rounded-full mr-5' src="data:image/jpeg;base64," alt="" />
         <p className='text-sm md:text-2xl'>{item.Title}</p>
       </div></Link>
      ))}
      </div>
    </div>
  )
}

export default Blogposts
